import { createStore } from 'redux';
import { createSlice } from '@reduxjs/toolkt';

const initialState = { counter: 0, showCounter: true };

//create as many slices as there are different pieces of state (here the counter state) not directly related (ex: if an authenticate state is added)
const counterSlice = createSlice({
  name: 'counter',   //a slice needs a name
  initialState,   // = initialState: initialState
  reducers: {    //it's an object of all the reducers this slice needs by adding methods that will automatically receive the latest state
    increment(state) {
      state.counter++;   //allowed here because Redux Toolkit internally use a package (immer) to clone the existing state in a new state object keeping not editing states and overriding editing states in an immutable way
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {     //if needed the reducer method can take action as an argument too to access an action payload
      state.counter = state.counter + action.amount;
    },

    toggle(state) {
      state.showCounter = !state.showCounter;
    }
  }
});

